import React,{ useState,userEffect, useEffect } from 'react';
import './App.css';
import  Axios  from 'axios';
import axios from 'axios';

//same of database
function App() {
  const[studentName,setStudentName]=useState('')
  const[Email,setEmail]=useState('')
  const[Age,setAge]=useState(0)
  const[cohort,setCohort]=useState('')

  //addStudent
  const addStudent=()=>{
    //path of .create server/index
    Axios.post('http://localhost:5000/create',{studentName,Email,Age,cohort})
  }

  //show
  //tansechhhh http://
  const[student,setStudent]=useState([])
  useEffect(()=>{
    Axios.get('http://localhost:5000/').then(res=>{
      setStudent(res.data)
    })
  })
  //delete
  const deleteStudent=(id)=>{
    Axios.delete(`http://localhost:5000/delete/${id}`)
  }
  //update
  const [newStudent, setNewStudent] = useState(0)
  const updateStudent=(id)=>{
    Axios.put('http://localhost:5000/update',{id, newStudent})

  }


  return (
    <div className="App">
      <h1>RBK</h1>
      <div id='input'>
      <label className='label' >Name:</label>
      <input placeholder='' className='x' type="text"  onChange={(e)=>{setStudentName(e.target.value)}}></input>

      <label className='label'>Email:</label>
      <input placeholder='' className='x' type="text"  onChange={(e)=>{setEmail(e.target.value)}}></input>

      <label className='label'>Age:</label>
      <input placeholder='' className='x' type="Number"  onChange={(e)=>{setAge(e.target.value)}}></input>

      <label className='label'>Cohort:</label>
      <input placeholder='' className='x' type="text"  onChange={(e)=>{setCohort(e.target.value)}}></input>


      <button onClick={addStudent} id='add'>ADD Student</button>
      </div>


      {
        student.map((val,key)=>{
          return <div key={key} id='s'>
            <h3>{val.studentName} | {val.Email} | {val.Age} | {val.cohort}</h3><button  className='delete'onClick={() =>{deleteStudent(val._id)}} id='delete'>X</button> <button className="update"  onClick={() => {updateStudent(val._id)}}>Update</button>

            <h3></h3>
            <h3></h3>
            <h3></h3>
          </div>
        })
      }
    </div>
  );
}

export default App;
