import React,{ useState,userEffect } from 'react';
import './App.css';
import  Axios  from 'axios';

//same of database
function App() {
  const[studentName,setStudentName]=useState('')
  const[Email,setEmail]=useState('')
  const[Age,setAge]=useState(0)
  const[cohort,setCohort]=useState('')

  const addStudent=()=>{
    //path of .create server/index
    Axios.post('http://localhost:5000/create',{studentName,Email,Age,cohort})
  }
  const[student,setStudent]=useState([])

  return (
    <div className="App">
      <label className='label'>studentName:</label>
      <input type="text"  onChange={(e)=>{setStudentName(e.target.value)}}></input>

      <label className='label'>Email:</label>
      <input type="text"  onChange={(e)=>{setEmail(e.target.value)}}></input>

      <label className='label'>Age:</label>
      <input type="Number"  onChange={(e)=>{setAge(e.target.value)}}></input>

      <label className='label'>cohort:</label>
      <input type="text"  onChange={(e)=>{setCohort(e.target.value)}}></input>

      <button onClick={addStudent}>ADD Student</button>
    </div>
  );
}

export default App;
